mapper.select:
select * from t_video where 1 = 1

    and id = #{id}


    and `name` = #{name}

select * from t_video
where isDelete = 0

    and id = #{id}


    and (`name` like "%"#{keyword}"%" or phone like "%"#{keyword}"%")


    and addTime >= #{startTime}


    and addTime <= #{endTime}

order by addTime desc
 limit #{start}, #{limit}
